import React from 'react';
import Link from '../../utils/ActiveLink';

const NavbarTop = () => {
    const [menu, setMenu] = React.useState(true)

    const toggleNavbar = () => {
        setMenu(!menu)
    }

    React.useEffect(() => {
        let elementId = document.getElementById("navbar");
        document.addEventListener("scroll", () => {
            if (window.scrollY > 170) {
                elementId.classList.add("is-sticky");
            } else {
                elementId.classList.remove("is-sticky");
            }
        });
        window.scrollTo(0, 0);
    })

    const classOne = menu ? 'collapse navbar-collapse' : 'collapse navbar-collapse show';
    const classTwo = menu ? 'navbar-toggler navbar-toggler-right collapsed' : 'navbar-toggler navbar-toggler-right';

    return (
        <React.Fragment>
            <div id="navbar" className="navbar-area" style={{ background: 'linear-gradient(to right, #2878EB, #F129C9)' }}>
                <div className="edemy-nav">
                    <div className="container-fluid">
                        <div className="navbar navbar-expand-lg navbar-light">

                            <Link href="/">
                                <a className="navbar-brand">
                                    <i class="fa fa-phone"></i> +11 0259 3654 2360
                                </a>
                            </Link>
                            <Link href="/">
                                <a className="navbar-brand">
                                    <i class="fa fa-envelope-o"></i>  info@website.com
                                </a>
                            </Link>
                            <div className={classOne} id="navbarSupportedContent">
                                <ul className="navbar-nav">
                                    <li className="nav-item">
                                        <Link href="#" activeClassName="active">
                                        <a href="#"><i class="fa fa-facebook" aria-hidden="true"></i></a>
                                        </Link>
                                    </li>

                                    <li className="nav-item">
                                        <Link href="#">
                                        <a href="#"><i class="fa fa-twitter" aria-hidden="true"></i></a>
                                        </Link>
                                    </li>

                                    <li className="nav-item megamenu">
                                        <Link href="#">
                                        <a href="#"><i class="fa fa-instagram" aria-hidden="true"></i></a>
                                        </Link>
                                    </li>

                                    <li className="nav-item">
                                        <Link href="#">
                                        <a href="#"><i class="fa fa-pinterest" aria-hidden="true"></i></a>
                                        </Link>
                                    </li>

                                    <li className="nav-item">
                                        <Link href="#">
                                            <a onClick={e => e.preventDefault()} className="nav-link">
                                                Blog <i className='bx bx-chevron-down'></i>
                                            </a>
                                        </Link>
                                    </li>

                                    <li className="nav-item">
                                        <Link href="#">
                                            <a onClick={e => e.preventDefault()} className="nav-link">
                                                Contact Us <i className='bx bx-chevron-down'></i>
                                            </a>
                                        </Link>
                                    </li>
                                </ul>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </React.Fragment>
    );
}

export default NavbarTop;
