import React from 'react';
import dynamic from 'next/dynamic';
const OwlCarousel = dynamic(import('react-owl-carousel3'));

const options = {
    loop: true,
    nav: true,
    margin: 60,
    dots: false,
    autoplayHoverPause: true,
    autoplay: true,
    navText: [
        "<i class='bx bx-chevron-left'></i>",
        "<i class='bx bx-chevron-right'></i>"
    ],
    responsive:{
        0:{
            items: 1,
            
        },
        // 600:{
        //     items: 3
        // },
        // 768:{
        //     items: 4,
        //     margin: 30,
        // },
        // 1000:{
        //     items: 6
        // }
    }
};

const Banner = () => {
    const [display, setDisplay] = React.useState(false);

    React.useEffect(() => {
        setDisplay(true);
    }, [])
    return (
        <div className="partner-area pt-100 pb-70 border-bottom">
            <div>
                {display ? <OwlCarousel 
                    className="partner-slides owl-carousel owl-theme"
                    {...options}
                >  
                    <div className="single-partner-item" style={{backgroundImage:"url(/images/bannerimg/2.png)"}}>
                        {/* <img src="/images/bannerimg/5.png" alt="image" /> */}
                          <img src="/images/bannerimg/5.png" alt="image" />
                    </div>

                    <div className="single-partner-item">
                        {/* <img src="/images/bannerimg/5.png" alt="image" /> */}
                          <img src="/images/bannerimg/2.png" alt="image" />
                    </div>
            
                </OwlCarousel> : ''}
            </div>
        </div>
    )
}

export default Banner;
